mapper.insert:
insert into user
    (user_id, user_password, user_name, email_id, email_domain)
values (#{userId},#{password},#{userName},#{emailId},#{emailDomain} );

mapper.select:
select user_no, user_id,user_name,email_id,email_domain, user_state
from user
where user_id = #{userId} and user_password = #{password};

select count(user_id)
from user
where user_state = 1 and user_id = #{userId};
